/* eslint-disable jsx-a11y/anchor-is-valid */
import React, { useEffect, useState } from "react";
import TMDBAPI from "../../api/tmdbapi";
import Search from "../common/search";
import { Button, Card, Col, Pagination, Row, Typography } from "antd";
import NAImg from "../../util/imgs/na.png";
import "./results.scss";

const { Title } = Typography;

function Results(props) {
  const [results, setResults] = useState([]);
  const [filter, setFilter] = useState("all");

  useEffect(() => {
    TMDBAPI.search(props.query, function (response) {
      if (response.status === 200) {
        setResults(response.data);
      } else {
        console.log("No Results!");
      }
    });
  }, [props.query]);

  return (
    <div className="results">
      <Search inputValue={props.query} />
      <Title level={4}>Search Results for "{props.query}"</Title>

      <Row>
        <Col span={8} style={{ float: "right" }}>
          <ResultsFilter
            filter={filter}
            setFilter={setFilter}
            results={results}
          />
        </Col>
        <Col span={12} style={{ float: "right" }}>
          <ResultsView
            query={props.query}
            results={results.filter((show) => show.type !== filter)}
          />
        </Col>
      </Row>
    </div>
  );
}

function ResultsView(props) {
  const [min, setMin] = useState(0);
  const [max, setMax] = useState(20);

  const handleChange = (value: number) => {
    setMin((value - 1) * 20);
    setMax(value * 20);
  };

  const handleClick = (id: number, title: string, media_type: string) => {
    console.log(id, title, media_type);
  };

  return (
    <div className="results-pagination">
      {props.results &&
        props.results.length > 0 &&
        props.results.slice(min, max).map((show) => (
          <Card key={show.id}>
            <Row>
              <Col span={6}>
                {show.img !== null && (
                  <img
                    src={show.img}
                    alt={show.title}
                    onClick={() => handleClick(show.id, show.title, show.type)}
                  />
                )}

                {show.img === null && (
                  <img
                    src={NAImg}
                    alt={show.title}
                    onClick={() => handleClick(show.id, show.title, show.type)}
                  />
                )}
              </Col>
              <Col span={16}>
                <a onClick={() => handleClick(show.id, show.title, show.type)}>
                  {show.title}
                </a>
                <h4>
                  {show.type === "movie" ? show.type : show.type + " show"}
                </h4>
                <span></span>
              </Col>
            </Row>
          </Card>
        ))}
      <Pagination
        defaultCurrent={1}
        total={props.results.length}
        defaultPageSize={20}
        onChange={handleChange}
      />
    </div>
  );
}

function ResultsFilter(props) {
  const handleClick = (filter: string) => {
    props.setFilter(filter);
  };

  return (
    <Card title="Filter" className="results-filter">
      <Button className="result-filter-btn" onClick={() => handleClick("all")}>
        <span style={{ float: "left" }}>
          <h2>All</h2>
        </span>
        <span style={{ float: "right" }}>
          <h3>{props.results.length}</h3>
        </span>
      </Button>
      <Button className="result-filter-btn" onClick={() => handleClick("tv")}>
      <span style={{ float: "left" }}>
          <h2>Movies</h2>
        </span>
        <span style={{ float: "right" }}>
          <h3>{props.results.filter(show => show.type )}</h3>
        </span>
      </Button>
      <Button
        className="result-filter-btn"
        onClick={() => handleClick("movie")}
      >
        TV Shows
      </Button>
    </Card>
  );
}

export default Results;
