import React, { useState } from "react";
import { Button, Card, Col, Modal, Row, Tabs, Typography } from "antd";
import NAImg from "../../util/imgs/na.png";

const { TabPane } = Tabs;
const { Title } = Typography;

function Overview(props) {
  console.log(props.show);
  return (
    <>
      {props.show && (
        <Card
          title={
            props.show.title +
            " (" +
            props.show.release_date.substring(0, 4) +
            ")"
          }
          className="details overview"
        >
          <Tabs defaultActiveKey="1">
            <TabPane tab="Details" key="1">
              <DetailsTab show={props.show} />
            </TabPane>
            <TabPane tab="Cast" key="2">
              Content of Tab Pane 2
            </TabPane>
            <TabPane tab="Recommendations" key="3">
              Content of Tab Pane 3
            </TabPane>
            <TabPane tab="Notes" key="4">
              Content of Tab Pane 3
            </TabPane>
          </Tabs>
        </Card>
      )}
    </>
  );
}

function DetailsTab(props) {
  const handleClick = (e) => {
    console.log("Jen was clicked");
  };
  const [modalVisible, setModalVisible] = useState(false);

  const showModal = () => {
    setModalVisible(true);
  };

  return (
    <div className="details-tab">
      {props.show && (
        <>
          <Row>
            <Col span={6}>
              {props.show.img !== null && (
                <img
                  src={props.show.img}
                  alt={props.show.title}
                  className="overview-img"
                />
              )}

              {props.show.img === null && (
                <img
                  src={NAImg}
                  alt={props.show.title}
                  className="overview-img"
                />
              )}
              <Button onClick={showModal} className="add-btn">
                Add To List
              </Button>
              <OverviewModal
                modalVisible={modalVisible}
                setModalVisible={setModalVisible}
                show={props.show}
              />
            </Col>
            <Col span={12}>
              <span>
                TMDB Rating: {props.show.rating} IMDB Rating: 8.7/10 1,487,477
                users
              </span>
              <Title level={4}>Synopsis</Title>
              <hr />
              <p>{props.show.overview}</p>
            </Col>
          </Row>
        </>
      )}
    </div>
  );
}

function OverviewModal(props) {
  const handleOk = () => {
    props.setModalVisible(false);
  };

  const handleCancel = () => {
    props.setModalVisible(false);
  };
  console.log(props.modalVisible);
  return (
    <>
      {props.show && (
        <Modal
          title={
            props.show.title +
            " (" +
            props.show.release_date.substring(0, 4) +
            ")"
          }
          visible={props.modalVisible}
          onOk={handleOk}
          onCancel={handleCancel}
        >
          <p>Some contents...</p>
          <p>Some contents...</p>
          <p>Some contents...</p>
        </Modal>
      )}
    </>
  );
}

export default Overview;
