import React, { useEffect, useState } from "react";
import TMDBAPI from "../../api/tmdbapi";
import Search from "../common/search";
import { Card, Col, Pagination, Row, Typography } from "antd";
import NAImg from "../../util/imgs/na.png";
import "./results.scss";

const { Title } = Typography;

function Results(props) {
  const [results, setResults] = useState([]);

  useEffect(() => {
    TMDBAPI.search(props.query, function (response) {
      if (response.status === 200) {
        console.log(response.data);
        setResults(response.data);
      } else {
        console.log("No Results!");
      }
    });
  }, [props.query]);

  return (
    <div className="results">
      <Search inputValue={props.query} />
      <ResultsView query={props.query} results={results} />
    </div>
  );
}

function ResultsView(props) {
  const [min, setMin] = useState(0);
  const [max, setMax] = useState(20);

  const handleError = (e) => {
    e.target.src = NAImg;
  };

  const handleChange = (value: number) => {
    setMin((value - 1) * 20);
    setMax(value * 20);
  };

  const handleClick = (id: number, title: string, media_type: string) => {
    console.log(id, title, media_type);
  }

  return (
    <div className="results-pagination">
      <Title level={4}>Search Results for "{props.query}"</Title>
      {props.results && props.results.length > 0 && (
        props.results.slice(min, max).map((show) => (
          <Card key={show.id}>
            <Row>
              <Col span={6}>
              <img
                  src={show.img}
                  alt={show.title}
                  onError={handleError}
                  onClick={() => handleClick(show.id, show.title, show.type)} />
              </Col>
            </Row>
          </Card>
        ))
      )}
    </div>
  );
}

export default Results;
