import React, { useState } from "react";
import { Button, Dropdown, Form, Menu, Modal } from "antd";

function WatchlistForm(props) {
  const [form] = Form.useForm();
  const [status, setState] = useState("Currently Watching");
  const [rating, setRating] = useState(0.0);
  const [comments, setComments] = useState("");

  const handleOk = () => {
    props.setModalVisible(false);
  };

  const handleCancel = () => {
    props.setModalVisible(false);
  };

  function handleMenuClick(e) {
    console.log("click", e);
  }
  const menu = (
    <Menu onClick={handleMenuClick}>
      <Menu.Item key="1">1st menu item</Menu.Item>
      <Menu.Item key="2">2nd menu item</Menu.Item>
      <Menu.Item key="3">3rd menu item</Menu.Item>
    </Menu>
  );

  return (
    <>
      {props.show && (
        <Modal
          title={
            props.show.title +
            " (" +
            props.show.release_date.substring(0, 4) +
            ")"
          }
          visible={props.modalVisible}
          onOk={handleOk}
          onCancel={handleCancel}
          footer={[
            <Button key="cancel" onClick={handleCancel}>
              Cancel
            </Button>,
            <Button key="submit" type="primary" onClick={handleOk}>
              Submit
            </Button>,
          ]}
        >
          <Form>
            <Form.Item label="Status">
              <Dropdown.Button  overlay={menu}>
                Dropdown
              </Dropdown.Button>
            </Form.Item>
            <Form.Item label="Overall Rating"></Form.Item>
            <Form.Item label="Comments"></Form.Item>
          </Form>
        </Modal>
      )}
    </>
  );
}

export default WatchlistForm;
