/* eslint-disable jsx-a11y/anchor-is-valid */
import React, { useState, useEffect } from "react";
import { Card, Col, Row } from "antd";
import TMDBAPI from "../../api/tmdbapi";
import "./page.scss";

function Page(props) {
  const [result, setResult] = useState([]);
  const [filter, setFilter] = useState("popular");

  useEffect(() => {
    TMDBAPI.get_tv(function (response) {
      setResult(response.data);
      console.log(props.type);
    });
  }, [props.type]);

  const handleFilter = (newFilter: string) => {
      setFilter(newFilter);
  }

  return (
    <div className="browse-page">
      <Row>
        <Col span={5}>
          <SideMenu type={props.type} onClick={handleFilter}/>
        </Col>
        <Col span={17}>
            <DisplayList list={result} />
        </Col>
      </Row>
    </div>
  );
}

function SideMenu(props) {
  return (
    <div className="side-menu-navigation">
      {props.type === "tv" && <h1>TV Shows</h1>}
      {props.type === "movies" && <h1>Movies</h1>}
      <ul>
        <li onClick={props.handleFilter}>
          <a>Popular</a>
        </li>
        <li>
          <a>Top Rated</a>
        </li>
        <li>
          <a>On Air</a>
        </li>
      </ul>
    </div>
  );
}
 
function DisplayList(props) {
    console.log(props.list)
    return (
        <Row>
            {props.list.map((show) => {
                <Col span={12}>
                    <Card title="Jen">

                    </Card>
                </Col>
            })}
        </Row>
    )
}

export default Page;
