import React, { useState, useEffect } from "react";
import WatchListAPI from "../../api/watchlistapi";
import { Tabs, Table } from "antd";
import "./watchlist.scss";

const { TabPane } = Tabs;
function WatchList() {
  const [watchlist, setList] = useState([]);
  
  useEffect(() => {
    WatchListAPI.retrieveAll(function (response) {
      setList(response.data);
      console.log(response.data);
    });
  }, []);

  return (
    <div className="watchlist-container">
      <Tabs defaultActiveKey="all">
        <TabPane tab="All Movies and Shows" key="All">
          <ShowAll watchlist={watchlist} />
        </TabPane>
        <TabPane tab="Currently Watching" key="Currently Watching">
          Content of Tab Pane 2
        </TabPane>
        <TabPane tab="Completed" key="Completed">
          Content of Tab Pane 3
        </TabPane>
        <TabPane tab="On Hold" key="On Hold">
          Content of Tab Pane 3
        </TabPane>
        <TabPane tab="Dropped" key="Dropped">
          Content of Tab Pane 3
        </TabPane>
        <TabPane tab="Plan to Watch" key="Plan to Watch">
          Content of Tab Pane 3
        </TabPane>
      </Tabs>
    </div>
  );
}

function ShowAll(props) {
  const columns = [
    {
      title: "Title",
      dataIndex: "show",
      sorter: {
        compare: (a, b) => a.show - b.show,
        multiple: 4,
      },
    },
    {
      title: "Status",
      dataIndex: "status",
      sorter: {
        compare: (a, b) => a.status - b.status,
        multiple: 3,
      },
    },
    {
      title: "Rating",
      dataIndex: "rating",
      sorter: {
        compare: (a, b) => a.rating - b.rating,
        multiple: 2,
      },
    },
    {
      title: "Date Added",
      dataIndex: "created_dt",
      sorter: {
        compare: (a, b) => a.created_df - b.created_dt,
        multiple: 1,
      },
    },
  ];
  return <Table columns={columns} dataSource={props.watchlist} />;
}

export default WatchList;
