import React, { useEffect, useState } from "react";
import { MovieDetails, ShowDetails, Statistics, Trailer } from "./details";
import { Col, Row } from "antd";
import Overview from "./overview";
import Search from "../common/search";
import TMDBAPI from "../../api/tmdbapi";
import "./show.scss";

function Show(props) {
  const [results, setResults] = useState([]);

  useEffect(() => {
    TMDBAPI.info(props.id, props.type, function (response) {
      console.log(response.data)
      if (response.status === 200) {
        console.log(response.data)
        setResults(response.data[0]);
      } else {
        console.log("No Results!");
      }
    });
  }, [props.id, props.type]);

  return (
    <div className="show-container">
      <Search inputValue={props.title} />

      {results && (
        <Row>
          <Col span={6}>
            {props.type === "movie" && <MovieDetails show={results} />}
            {props.type === "tv" && <ShowDetails show={results} />}
            <Statistics show={results} />
            <Trailer show={results} />
          </Col>
          <Col span={15}>
            <Overview show={results} />
          </Col>
        </Row>
      )}
    </div>
  );
}

export default Show;
