import React, { useState, useEffect } from "react";
import WatchListAPI from "../../api/watchlistapi";
import { Tabs, Table } from "antd";
import "./watchlist.scss";

const { TabPane } = Tabs;
function WatchList() {
  const [watchlist, setList] = useState([]);
  const columns = [
    {
      title: "Title",
      dataIndex: "title",
      sorter: {
        compare: (a, b) => a.show - b.show,
        multiple: 4,
      },
    },
    {
      title: "Status",
      dataIndex: "status",
      sorter: {
        compare: (a, b) => a.status - b.status,
        multiple: 3,
      },
    },
    {
      title: "Rating",
      dataIndex: "rating",
      sorter: {
        compare: (a, b) => a.rating - b.rating,
        multiple: 2,
      },
    },
    {
      title: "Date Added",
      dataIndex: "created_dt",
      sorter: {
        compare: (a, b) => a.created_df - b.created_dt,
        multiple: 1,
      },
    },
  ];

  useEffect(() => {
    WatchListAPI.retrieveAll(function (response) {
      setList(response.data);
      console.log(response.data)
    });
  }, []);

  return (
    <div className="watchlist-container">
      <Tabs defaultActiveKey="all">
        <TabPane tab="All Movies and Shows" key="All">
          <ShowLists watchlist={watchlist} columns={columns} />
        </TabPane>
        <TabPane tab="Currently Watching" key="Currently Watching">
        <ShowLists watchlist={watchlist.filter(show => show.status === "Currently Watching")} columns={columns} />
        </TabPane>
        <TabPane tab="Completed" key="Completed">
        <ShowLists watchlist={watchlist.filter(show => show.status === "Completed")} columns={columns} />
        </TabPane>
        <TabPane tab="On Hold" key="On Hold">
        <ShowLists watchlist={watchlist.filter(show => show.status === "On Hold")} columns={columns} />
        </TabPane>
        <TabPane tab="Dropped" key="Dropped">
        <ShowLists watchlist={watchlist.filter(show => show.status === "Dropped")} columns={columns} />
        </TabPane>
        <TabPane tab="Plan to Watch" key="Plan to Watch">
        <ShowLists watchlist={watchlist.filter(show => show.status === "Plan to Watch")} columns={columns} />
        </TabPane>
      </Tabs>
    </div>
  );
}

function ShowLists(props) {
  return (
    <Table
      columns={props.columns}
      dataSource={props.watchlist}
      rowKey={(record) => record.id}
    />
  );
}



export default WatchList;
