import axios from "axios";
import { callbackify } from "util";
import AsyncStorage from "@react-native-async-storage/async-storage";


class UserAPI {
  static  getToken = async () => {
    try {
      const value = await AsyncStorage.getItem("@token");
      if (value !== null) {
        return value;
      }
    } catch (e) {}
  };

  
  static async login(username, password, callback) {
    const config = {
      headers: {
        "Content-Type": "application/json",
      },
    };

    const body = JSON.stringify({ username, password });
    try {
      await axios
        .post("http://127.0.0.1:8000/api/auth/logins/", body, config)
        .then((res) => {
          callback(res.data);
        });
    } catch (error) {
      console.log(error.response);
      callback(error.response.status);
    }
  }

  static async register(username, email, password, callback) {
    const config = {
      headers: {
        "Content-Type": "application/json",
      },
    };

    const body = JSON.stringify({ username, email, password });
    try {
      await axios
        .post("http://127.0.0.1:8000/api/auth/register/", body, config)
        .then((res) => {
          callback(res.data);
        });
    } catch (error) {
      console.log(error.response);
      callback(error.response.status);
    }
  }

  static async logout() {
    try {
      await axios.get("http://127.0.0.1:8000/api/auth/logout/");
    } catch (error) {
      callbackify(error.response.status);
    }
  }

  static tokenConfig = (state) => {
    const token = state().auth.token;

    const config = {
      headers: {
        "Content-Type": "application/json",
      },
    };

    if (token) {
      config.headers["Authorization"] = `Token ${token}`;
    }
    return config;
  };
}

export default UserAPI;
