import React from "react";
import { useHistory } from "react-router-dom";
import { Dropdown, Menu, Row, Typography } from "antd";
import { useStore } from "../../util/store/mycontext";
import { useObserver } from "mobx-react-lite";

import "./navbar.scss";

const { Title } = Typography;

function Navbar() {
  const history = useHistory();
  const userStore = useStore().userStore;

  const handleClick = (e) => {
    if (e.key === "shows" || e.key === "movies") {
      history.push("/" + e.key + "/browse");
    } else if (e.key === "home") {
      history.push("/");
    } else if (e.key === "signup") {
      history.push("/signup");
    } else if (e.key === "signin") {
      history.push("/signin");
    } else {
      history.push("/mylists");
    }
  };

  const menu = (
    <Menu>
      <Menu.Item>
        <a target="_blank" rel="noopener noreferrer" href="http://www.alipay.com/">
          1st menu item
        </a>
      </Menu.Item>
      <Menu.Item>
        <a target="_blank" rel="noopener noreferrer" href="http://www.taobao.com/">
          2nd menu item
        </a>
      </Menu.Item>
      <Menu.Item>
        <a target="_blank" rel="noopener noreferrer" href="http://www.tmall.com/">
          3rd menu item
        </a>
      </Menu.Item>
      <Menu.Item danger>a danger item</Menu.Item>
    </Menu>
  );

  return useObserver(() => (
    <nav>
      <Row>
        <Menu onClick={handleClick} mode="horizontal" className="navbar">
          <Menu.Item className="left" key="home">
            <Title level={3}>MyShowLists</Title>
          </Menu.Item>
          <Menu.Item key="shows">TV</Menu.Item>
          <Menu.Item key="movies">Movies</Menu.Item>
          <Menu.Item key="lists">My Watch Lists</Menu.Item>
          {!userStore.state.isAuthenticated && (
            <>
              <Menu.Item key="signin" style={{ float: "right" }}>
                Sign In
              </Menu.Item>
              <Menu.Item key="signup" style={{ float: "right" }}>
                Sign Up
              </Menu.Item>
              <Dropdown overlay={menu}>
    <a className="ant-dropdown-link" onClick={e => e.preventDefault()}>
      Hover me 
    </a>
  </Dropdown>
            </>
          )}

          {userStore.state.isAuthenticated && (
            <>
              <Menu.Item key="profile" style={{ float: "right" }}>
                {userStore.state.user.username}
              </Menu.Item>
            </>
          )}
        </Menu>
      </Row>
    </nav>
  ));
}

export default Navbar;
