import React, { useState } from "react";
import {
  Button,
  Card,
  Checkbox,
  Col,
  Divider,
  Form,
  Input,
  Row,
  Typography,
} from "antd";
import { EyeInvisibleOutlined, EyeTwoTone } from "@ant-design/icons";
import UserAPI from "../../api/authapi";
import { useStore } from "../../util/store/mycontext";
import { useHistory } from "react-router-dom";
import "./forms.scss";

const { Title } = Typography;

function Signin() {
  const [form] = Form.useForm();

  const [email, setEmail] = useState("");
  const [username, setUser] = useState("");
  const [password, setPass] = useState("");
  const userStore = useStore().userStore;
  const history = useHistory();

  const handleLogin = () => {
    UserAPI.register(username, email, password, function (response) {
      if (response !== 400) {
        userStore.setLoginState(response.user, response.token);
        history.push("/");
      }
    });
  };

  return (
    <Card className="form">
      <Row>
        <Col span={10}>
          <Form layout={"vertical"} form={form}>
            <Title level={3}>Sign Up</Title>
            <Form.Item label="Username:">
              <Input
                placeholder="Your username"
                value={username}
                onChange={(e) => setUser(e.target.value)}
              />
            </Form.Item>
            <Form.Item label="Email:">
              <Input
                placeholder="E.g email@example.com"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
            </Form.Item>
            <Form.Item label="Password:">
              <Input.Password
                placeholder="Your password"
                value={password}
                iconRender={(visible) =>
                  visible ? <EyeTwoTone /> : <EyeInvisibleOutlined />
                }
                onChange={(e) => setPass(e.target.value)}
              />
            </Form.Item>
            <Form.Item>
              <Checkbox>
                I agree to the Nonexistent Terms of Service and Privacy Policy{" "}
              </Checkbox>
            </Form.Item>
            <Form.Item>
              <Button className="form-btn submit" onClick={handleLogin}>
                SIGN UP
              </Button>
            </Form.Item>
          </Form>
        </Col>
        <Divider type="vertical" />
      </Row>
    </Card>
  );
}

export default Signin;
