import React, { useState } from "react";
import { Avatar, Button, Card, Col, Row, Tabs, Typography } from "antd";
import WatchlistForm from "../forms/watchlistform";
import { useStore } from "../../util/store/mycontext";
import NAImg from "../../util/imgs/na.png";

const { TabPane } = Tabs;
const { Title } = Typography;

function Overview(props) {
  console.log(props.show);

  return (
    <>
      {props.show && (
        <Card
          title={
            props.show.title +
            " (" +
            props.show.release_date.substring(0, 4) +
            ")"
          }
          className="details overview"
        >
          <Tabs defaultActiveKey="1">
            <TabPane tab="Details" key="1">
              <DetailsTab show={props.show} />
              <Providers />
              <Cast />
            </TabPane>
            <TabPane tab="Cast" key="2">
              Content of Tab Pane 2
            </TabPane>
            <TabPane tab="Recommendations" key="3">
              Content of Tab Pane 3
            </TabPane>
          </Tabs>
        </Card>
      )}
    </>
  );
}

function DetailsTab(props) {
  const [modalVisible, setModalVisible] = useState(false);
  const userStore = useStore().userStore;

  // TODO only show modal if user is authenticated otherwise redirect user
  const showModal = () => {
    if (userStore.state.isAuthenticated) {
      setModalVisible(true);
    } else {
      console.log("Todo");
    }
  };

  return (
    <div className="details-tab">
      {props.show && (
        <>
          <Row>
            <Col span={8}>
              {props.show.img !== null && (
                <img
                  src={props.show.img}
                  alt={props.show.title}
                  className="overview-img"
                />
              )}

              {props.show.img === null && (
                <img
                  src={NAImg}
                  alt={props.show.title}
                  className="overview-img"
                />
              )}
              <Button onClick={showModal} className="add-btn">
                Add To List
              </Button>
              <WatchlistForm
                modalVisible={modalVisible}
                setModalVisible={setModalVisible}
                show={props.show}
              />
            </Col>
            <Col span={16}>
              <div className="watchlist-ratings">
                <Avatar shape={"square"} size={64} icon={props.show.rating} />
                <span className="watchlist-rater">TMDB Rating </span>

                <Avatar shape={"square"} size={64} icon={8.7} />
                <span className="watchlist-rater">
                  IMDB Rating from 1,487,477 users
                </span>
              </div>

              <Title level={4}>Synopsis</Title>
              <hr />
              <p>{props.show.overview}</p>
              <li>
                <ul>
                  <strong>Genres:</strong> {props.show.genres}
                </ul>
              </li>
            </Col>
          </Row>
        </>
      )}
    </div>
  );
}

// TODO see if this is necessary
function Providers(props) {
  return <Card title="Where to watch" className="additional-cards"></Card>;
}

function Cast(props) {
  return <Card title="Cast \& Credit" className="additional-cards"></Card>;
}

export default Overview;
