import React, { useState } from "react";
import {
  Button,
  Card,
  Checkbox,
  Col,
  Divider,
  Input,
  Row,
  Space,
  Typography,
} from "antd";
import { EyeInvisibleOutlined, EyeTwoTone } from "@ant-design/icons";
import UserAPI from "../../api/userapi";
import { useStore } from "../../util/store/mycontext";
import { useHistory } from "react-router-dom";
import "./forms.scss";

const { Text, Title } = Typography;

function Signin() {
  const [username, setUser] = useState("");
  const [password, setPass] = useState("");
  const userStore = useStore().userStore;

  const handleLogin = () => {
  
    UserAPI.login(username, password, function(response) {
        if (response === 400) {
            console.log("Jennifer gonna get fucked")
        } else {
            userStore.setLoginState(response.user, response.token);
            history.pushState("/")
        }
    })
  };

  return (
    <Card className="signup-form">
      <Row>
        <Col span={10}>
          <Space direction="vertical" size="large">
            <Title level={3}>Sign in</Title>

            <div>
              <Text>Username</Text>
              <Input
                placeholder="Your username"
                value={username}
                onChange={(e) => setUser(e.target.value)}
              />
            </div>

            <div>
              <Text>Password</Text>
              <Input.Password
                placeholder="Your password"
                value={password}
                iconRender={(visible) =>
                  visible ? <EyeTwoTone /> : <EyeInvisibleOutlined />
                }
                onChange={(e) => setPass(e.target.value)}
              />
            </div>

            <Checkbox onChange={(e) => console.log(e.target.checked)}>
              Remember me
            </Checkbox>
          </Space>
          <Button className="form-btn submit" onClick={handleLogin}>
            SIGN IN
          </Button>
          <Button className="form-btn forgot-pass" ghost>
            Forgot your password?
          </Button>
        </Col>
        <Divider type="vertical" />
      </Row>
    </Card>
  );
}

export default Signin;
