import React from "react";
import { useHistory } from "react-router-dom";
import { Menu, Row, Typography } from "antd";
import { useStore } from "../../util/store/mycontext";
import { useObserver } from "mobx-react-lite";
import AsyncStorage from '@react-native-async-storage/async-storage';


import "./navbar.scss";

const { Title } = Typography;

function Navbar() {
  const history = useHistory();
  const userStore = useStore().userStore;

  const handleClick = (e) => {
    if (e.key === "shows" || e.key === "movies") {
      history.push("/" + e.key + "/browse");
    } else if (e.key === "home") {
      history.push("/");
    } else if (e.key === "signup") {
      history.push("/signup");
    } else if (e.key === "signin") {
      history.push("/signin");
    } else {
      history.push("/mylists");
    }
  };

  const getToken = async () => {
    try {
      const value = await AsyncStorage.getItem("@token");
      if (value !== null) {
        console.log(value)
        return value;
      }
    } catch (e) {}
  };

  getToken()

  return useObserver(() => (
    <nav>
      <Row>
        <Menu onClick={handleClick} mode="horizontal" className="navbar">
          <Menu.Item className="left" key="home">
            <Title level={3}>MyShowLists</Title>
          </Menu.Item>
          <Menu.Item key="shows">TV</Menu.Item>
          <Menu.Item key="movies">Movies</Menu.Item>
          <Menu.Item key="lists">My Watch Lists</Menu.Item>
          {!userStore.state.isAuthenticated && (
            <>
              <Menu.Item key="signin" style={{ float: "right" }}>
                Sign In
              </Menu.Item>
              <Menu.Item key="signup" style={{ float: "right" }}>
                Sign Up
              </Menu.Item>
            </>
          )}

          {userStore.state.isAuthenticated && (
            <>
              <Menu.Item key="profile" style={{ float: "right" }}>
                {userStore.state.user.username}
              </Menu.Item>
            </>
          )}
        </Menu>
      </Row>
    </nav>
  ));
}

export default Navbar;
