/* eslint-disable jsx-a11y/anchor-is-valid */
import React, { useEffect } from "react";
import { useHistory } from "react-router-dom";
import { Dropdown, Menu, Row, Typography } from "antd";
import { useStore } from "../../util/store/mycontext";
import AsyncStorage from "@react-native-async-storage/async-storage";
import { useObserver } from "mobx-react-lite";
import UserAPI from "../../api/authapi";
import "./navbar.scss";

const { Title } = Typography;

function Navbar() {
  const history = useHistory();
  const userStore = useStore().userStore;

  useEffect(() => {
    let token, user;

    const getToken = async () => {
      const value = await AsyncStorage.getItem("@token");
      return value;
    }
    
    const getUser = async () => {
      const value = await AsyncStorage.getItem("@user");
      return value;
    }

    token = getToken();    
    user = getUser()

    //userStore.setLoginState(user, token);

  }, [])
  console.log(userStore.state)


  const handleClick = (e) => {
    if (e.key === "shows" || e.key === "movies") {
      history.push("/" + e.key + "/browse");
    } else if (e.key === "home") {
      history.push("/");
    } else if (e.key === "signup") {
      history.push("/signup");
    } else if (e.key === "signin") {
      history.push("/signin");
    } else if (e.key === "lists") {
      history.push("/mylists");
    } else if (e.key === "profile") {
      // TODO "/'user'/profile"
      history.push("/profile");
    } else if (e.key === "settings") {
      // TODO "/'user'/settings"
      history.push("/settings");
    } else if (e.key === "logout") {
      UserAPI.logout();
      userStore.setLogoutState();
    }
  };

  const menu = (
    <Menu onClick={handleClick}>
      <Menu.Item key="profile">
        <a>My Profile</a>
      </Menu.Item>
      <Menu.Item key="settings">
        <a>Settings</a>
      </Menu.Item>
      <Menu.Item key="logout">
        <a>Logout</a>
      </Menu.Item>
    </Menu>
  );

  return useObserver(() => (
    <nav>
      <Row>
        <Menu onClick={handleClick} mode="horizontal" className="navbar">
          <Menu.Item className="left" key="home">
            <Title level={3}>MyShowLists</Title>
          </Menu.Item>
          <Menu.Item key="shows">TV</Menu.Item>
          <Menu.Item key="movies">Movies</Menu.Item>
          <Menu.Item key="lists">My Watch Lists</Menu.Item>
          {!userStore.state.isAuthenticated && (
            <>
              <Menu.Item key="signin" style={{ float: "right" }}>
                Sign In
              </Menu.Item>
              <Menu.Item key="signup" style={{ float: "right" }}>
                Sign Up
              </Menu.Item>
            </>
          )}

          {userStore.state.isAuthenticated && (
            <>
              <Dropdown overlay={menu} trigger={["click"]}>
                <a>{userStore.state.user.username}</a>
              </Dropdown>
            </>
          )}
        </Menu>
      </Row>
    </nav>
  ));
}

export default Navbar;
