export function createUserStore() {
  return {
    state: {
      isLoading: false,
      isAuthenticated: false,
      user: null,
      token: localStorage.getItem("token"),
    },

    setState(state: Object) {
        this.state = state;
    },

    logoutState: {
      isLoading: false,
      isAuthenticated: false,
      user: null,
      token: null,
    },
    //      localStorage.removeItem("token");

    loginFail: {
      isAuthenticated: false,
      user: null,
      token: null,
    },

    loginSuccess: {
      isLoading: false,
      isAuthenticated: false,
      user: null,
      token: localStorage.getItem("token"),
    },

    setLoginState(user, token) {
        
      let state = {
        isLoading: false,
        isAuthenticated: true,
        user: user,
        token: token,
      };
    },
  };
}
