export function createUserStore() {
  return {
    initialState: {
      isLoading: false,
      isAuthenticated: false,
      user: null,
      token: localStorage.getItem("token"),
    },

    logoutState: {
      isLoading: false,
      isAuthenticated: false,
      user: null,
      token: null,
    },

    loginFail: {
      isAuthenticated: false,
      user: null,
      token: null,
    },

    loginSuccess: {
      isLoading: false,
      isAuthenticated: false,
      user: null,
      token: localStorage.getItem("token"),
    },
  };
}
