/* eslint-disable jsx-a11y/anchor-is-valid */
import React, { useState } from "react";
import { Avatar, Button, Card, Col, Row, Tabs, Typography } from "antd";
import WatchlistForm from "../forms/watchlistform";
import { useStore } from "../../util/store/mycontext";
import NAImg from "../../util/imgs/na.png";

const { TabPane } = Tabs;
const { Paragraph, Title } = Typography;

function Overview(props) {
  const [key, setKey] = useState("details");

  const handleKey = (key: string) => {
    setKey(key);
  };

  co

  return (
    <>
      {props.show && (
        <Card
          title={
            props.show.title +
            " (" +
            props.show.release_date.substring(0, 4) +
            ")"
          }
          className="details overview"
        >
          <Tabs activeKey={key} onChange={handleKey}>
            <TabPane tab="Details" key="details">
              <DetailsTab show={props.show} />
              <Providers />
              <Cast cast={props.show.cast} setKey={setKey} />
            </TabPane>
            <TabPane tab="Cast" key="cast">
              Content of Tab Pane 2
            </TabPane>
            <TabPane tab="Recommendations" key="recommendations">
              Content of Tab Pane 3
            </TabPane>
          </Tabs>
        </Card>
      )}
    </>
  );
}

function DetailsTab(props) {
  const [modalVisible, setModalVisible] = useState(false);
  const [ellipsis, setEllipsis] = useState(true);
  const [ellipsisText, setText] = useState("more");
  const userStore = useStore().userStore;

  // TODO only show modal if user is authenticated otherwise redirect user
  const showModal = () => {
    if (userStore.state.isAuthenticated) {
      setModalVisible(true);
    } else {
      console.log("Todo");
    }
  };

  const handleEllipsis = () => {
    if (ellipsis === false) {
      setText("more");
    } else {
      setText("less");
    }
    setEllipsis(!ellipsis);
  };

  return (
    <div className="details-tab">
      {props.show && (
        <>
          <Row>
            <Col span={8}>
              {props.show.img !== null && (
                <img
                  src={props.show.img}
                  alt={props.show.title}
                  className="overview-img"
                />
              )}

              {props.show.img === null && (
                <img
                  src={NAImg}
                  alt={props.show.title}
                  className="overview-img"
                />
              )}
              <Button onClick={showModal} className="add-btn">
                Add To List
              </Button>
              <WatchlistForm
                modalVisible={modalVisible}
                setModalVisible={setModalVisible}
                show={props.show}
              />
            </Col>
            <Col span={16}>
              <div className="watchlist-ratings">
                <Avatar shape={"square"} size={64} icon={props.show.rating} />
                <span className="watchlist-rater">TMDB Rating </span>

                <Avatar
                  shape={"square"}
                  size={64}
                  icon={props.show.imdb.imdb_rating}
                />
                <span className="watchlist-rater">
                  IMDB Rating from {props.show.imdb.votes} votes
                </span>
              </div>

              <Title level={4}>Synopsis</Title>
              <hr />
              <p>{props.show.overview}</p>
              <p>(Source: TMDB)</p>

              {props.show.imdb.synopsis && (
                <>
                  <Paragraph ellipsis={ellipsis}>
                    {props.show.imdb.synopsis}
                  </Paragraph>
                  <a onClick={handleEllipsis}>{ellipsisText}</a>
                  <p>(Source: IMDB Synopsis)</p>
                </>
              )}

              <li>
                <ul>
                  <strong>Genres:</strong> {props.show.genres}
                </ul>
              </li>
            </Col>
          </Row>
        </>
      )}
    </div>
  );
}

// TODO see if this is necessary
function Providers() {
  return <Card title="Where to watch" className="additional-cards"></Card>;
}

function Cast(props) {
  const setKey = () => {
    props.setKey("cast");
  };

  return (
    <Card title="Cast & Credit" className="additional-cards">
      <Row>
        {props.cast &&
          props.cast.slice(0, 6).map((cast) => (
            <Col span={8}>
              <img src={cast["img"]} alt="n/a" />
              <div className="cast-member-info">
                <span>
                  <a>{cast["name"]}</a>
                  <p>{cast["character"]}</p>
                </span>
              </div>
            </Col>
          ))}
      </Row>
      <hr />
      <a onClick={setKey}>View All</a>
    </Card>
  );
}

export default Overview;
